(function($){"use strict";wp.customize('blogname',function(value){value.bind(function(to){$('.site-title a').text(to);});});wp.customize('blogdescription',function(value){value.bind(function(to){$('.site-description').text(to);});});wp.customize('graphy_logo_margin_top',function(value){value.bind(function(newval){$('.site-logo').css('margin-top',newval+'px');});});wp.customize('graphy_logo_margin_bottom',function(value){value.bind(function(newval){$('.site-logo').css('margin-bottom',newval+'px');});});})(jQuery);